import React from "react"
import { Container } from "react-bootstrap"
import { useFolder } from "../../hooks/useFolder"
import AddFolderButton from "./AddFolderButton"
import AddFileButton from "./AddFileButton"
import Folder from "./Folder"
// import File from "./File"
import Navbar from "./Navbar"
import FolderBreadcrumbs from "./FolderBreadcrumbs"
import { useParams, useLocation } from "react-router-dom"
import { SRLWrapper } from "simple-react-lightbox";
import TableDetails from "../table-details/TableDetails"

export default function Dashboard() {
  const { folderId } = useParams()
  const { state = {} } = useLocation()
  const { folder, childFolders, childFiles } = useFolder(folderId, state.folder)

  return (
    <>
      <Navbar />
      <Container fluid>
        <div className="d-flex align-items-center mx-4">
          <FolderBreadcrumbs currentFolder={folder} />
          <AddFileButton currentFolder={folder} />
          <AddFolderButton currentFolder={folder} />
        </div>
        {childFolders.length > 0 && (
          <div className="d-flex flex-wrap mx-4">
            {childFolders.map(childFolder => (
              <div
                key={childFolder.id}
                style={{ maxWidth: "250px" }}
                className="p-2"
              >
                <Folder folder={childFolder} />
              </div>
            ))}
          </div>
        )}
        {childFolders.length > 0 && childFiles.length > 0 && <hr />}
        {childFiles.length > 0 && (

          <div className="row m-4">
            {/* --Table Details-- */}
              <div className="col-lg-4 col-md-12 col-sm-12 pl-2 TableDetails p-4">
                <div><h2>Crane 1</h2></div>
                <div><h4 className="font-weight-bold py-2">Machine Information</h4></div>
                  <TableDetails/>
              </div>
            {/*--End Table Details-- */}

            {/*--Photo section-- */}
            <SRLWrapper className="col-lg-8 col-md-12 col-sm-12 mt-5">
              <div id="app" className="d-flex flex-wrap justify-content-end mt-5">
                {childFiles.map(childFile => (
                  <div // key={childFile.id} style={{ maxWidth: "400px" }}
                    className="m-3"
                  >
                    {/* {<File file={childFile}/>} */}
                      <a href={childFile.url}  ><img src={childFile.url} style={{ maxWidth: "350px" }} /></a>
                  </div>
              //--End of Photo section--

                ))}
              </div>
            </SRLWrapper>
          </div>

        )}
      </Container>
    </>
  )
}
