import React from "react";
import { Button } from "react-bootstrap";
import { MdOutlineCancel } from "react-icons/md";
import { FaRegSave } from "react-icons/fa";



const EditableRow = ({
  editFormData,
  handleEditFormChange,
  handleCancelClick,
}) => {
  return (
    <>
      <tr className="mr-3 d-flex flex-wrap table">
        <td className="px-0 ">
          <input
            type="text"
            required="required"
            placeholder="Enter a Specification"
            name="fullName"
            value={editFormData.fullName}
            onChange={handleEditFormChange}
            className="form-control"
          ></input>
        </td>
        <td className="px-1">
          <input
            type="text"
            required="required"
            placeholder="Enter an Detail"
            name="address"
            value={editFormData.address}
            onChange={handleEditFormChange}
            className="form-control "
          ></input>
        </td>
        <td className="d-flex flex-wrap">
          <Button type="submit" className="py-1 px-2 mr-2" variant="outline-secondary"><FaRegSave /></Button>
          <Button type="button" className="py-1 px-2" variant="outline-secondary" onClick={handleCancelClick}>
          <MdOutlineCancel />
          </Button>
        </td>
      </tr>
    </>
  );
};

export default EditableRow;
