import React, {useEffect, useState} from 'react';
import fireDb from "../../firebase";
import { Link } from "react-router-dom";
import "./Home.css"
import { toast } from 'react-toastify';
import AddEdit from "./AddEdit"
import "./HideAddEdit.js"
import { render } from '@testing-library/react';
import { useParams, useLocation } from "react-router-dom";
import { useFolder } from "../../hooks/useFolder";
import TableDetails from './TableDetails';
import { FaRegEdit, FaRegTrashAlt } from "react-icons/fa";






const Home = () => {
  const [data, setData] = useState({});
  const { folderId } = useParams()
  const { state = {} } = useLocation()
  const { folder } = useFolder(folderId, state.folder)

  // console.log(fireDb.child("tableDetail").val);  
  useEffect(() => {
      fireDb.child("tableDetail").on("value", (snapshot) => {
          if(snapshot.val()!== null){
            setData({...snapshot.val()});
          }else {
            setData({});
          }  
      });
      return() => {
        setData({});
      };
  }, []);





  //Delete Function
  const onDelete = (id) => {
    if(window.confirm("Are you sure that you wante to delete this information ?")){
      fireDb.child(`tableDetail/${id}`).remove((err) => {
        if(err){
          toast.error(err)
        }else{
          toast.success("Information deleted Successfully!");
        }
      })
    }
  }
  return (
    <div>
      <table className='styled-table d-flex w-100'>
        <tbody > 
            {Object.keys(data).map((id) => {

              {/* // {(() => {
              //   if (fireDb.child(`tableDetail/${id}`) !== null) {
              //     return (
              //     null
              //     )
              //   } else {
              //     return (
              //     <TableDetails/>
              //     )
              //   }
              // })()} */}
              return(
                <tr key={id} >
                  <td>{data[id].name}</td>
                  <td>{data[id].email}</td>
                  <td>
                    <Link to={`/update/${id}`}>
                      <button className='btn btn-edit' title="Edit">
                        <FaRegEdit size={17}/>
                      </button>
                    </Link>
                      <button className='btn btn-delete' title="Delete" onClick={() => onDelete(id)}>
                        <FaRegTrashAlt size={17}/>
                      </button>
                  </td>
                </tr>
              )
            })}
        </tbody>
      </table>
    </div>
    
  );

}


export default Home;
