import firebase from "firebase/app"
import "firebase/auth"
import "firebase/firestore"
import "firebase/storage"
import "firebase/database";


const app = firebase.initializeApp({
  apiKey: "AIzaSyCfKi43n71-dtUOtbCrAekflafGkf_GXLg",
  authDomain: "cornelderplatform.firebaseapp.com",
  projectId: "cornelderplatform",
  storageBucket: "cornelderplatform.appspot.com",
  messagingSenderId: "307388420536",
  appId: "1:307388420536:web:2f83cb5ff19d88d7d13f4d",
  databaseURL: "https://cornelderplatform-default-rtdb.firebaseio.com",
});



const firestore = app.firestore()
export const database = {
  folders: firestore.collection("folders"),
  files: firestore.collection("files"),
  formatDoc: doc => {
    return { id: doc.id, ...doc.data() }
  },
  getCurrentTimestamp: firebase.firestore.FieldValue.serverTimestamp,
}

export const storage = app.storage()
export const auth = app.auth()
export default app.database().ref()
