import React, {useEffect, useState} from 'react';
import fireDb from "../../firebase";
import { Link } from "react-router-dom";
import "./Home.css"
import { toast } from 'react-toastify';
import AddEdit from "./AddEdit"
import "./HideAddEdit.js"
import { render } from '@testing-library/react';




const Home = () => {
  const [data, setData] = useState({});

  useEffect(() => {
    fireDb.child("tableDetail").on("value", (snapshot) => {
      if(snapshot.val()!== null){
        setData({...snapshot.val()});
      }else {
        setData({});
      }
    });
    return() => {
      setData({});
    };
  }, [folderId]);


  //Delete Function
  const onDelete = (id) => {
    if(window.confirm("Are you sure that you wante to delete this information ?")){
      fireDb.child(`tableDetail/${id}`).remove((err) => {
        if(err){
          toast.error(err)
        }else{
          toast.success("Information deleted Successfully!");
        }
      })
    }
  }
  return (
    <div style={{marginTop: "100px"}}>
      <table className='styled-table'>
        <thead>
          <tr>
            <th>Specification</th>
            <th>Details</th>
            <th>Action</th>
          </tr>

        {/* <button onclick="toggleClock()" id="clockButton">Add</button> */}
        
        </thead>
        <tbody>
          {Object.keys(data).map((id) => {
            return(
              <tr key={id}>
                <td>{data[id].name}</td>
                <td>{data[id].email}</td>
                <td>
                  <Link to={`/update/${id}`}>
                    <button className='btn btn-edit'>Edit</button>
                  </Link>

                    <button className='btn btn-delete' onClick={() => onDelete(id)}>Delete</button>

                </td>
              </tr>
            )
          })}
        </tbody>
      </table>
      {/* <div id='clock'>
        <AddEdit/>
      </div> */}
    </div>
    
  );

}


export default Home;
