import React, {useEffect, useState} from 'react';
import { useHistory, useParams} from "react-router-dom";
import "./AddEdit.css";
import fireDb from "../../firebase";
import {toast} from "react-toastify";


const initialState = {
  name: "",
  email: "",
}

const AddEdit = () => {
  const [state, setState] = useState(initialState);
  const [data, setData] = useState({});

  const {name, email} = state;

  const history = useHistory();


  const {id} = useParams();

  useEffect(() => {
    fireDb.child("tableDetail").on("value", (snapshot) => {
      if(snapshot.val()!== null){
        setData({...snapshot.val()});
      }else {
        setData({});
      }
    });
    return() => {
      setData({});
    };
  }, [id]);

  useEffect(() =>{
    if(id){
      setState({...data[id]})
    }else {
      setState({...initialState})
    }
    return() => {
      setState({...initialState})
    }
  }, [id, data])



  const handleInputChange = (e) => {
    const {name, value} = e.target;
    setState({ ...state, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if(!name || !email){
      toast.error("Please provied value in each input field!")
    } else{
        if(!id){
          fireDb.child("tableDetail").push(state, (err) => {
            if(err){
              toast.error(err);
            }else{
              toast.success("Information Added Successfully");
            }
          });    
        }else{
            fireDb.child(`tableDetail/${id}`).set(state, (err) => {
              if(err){
                toast.error(err);
              }else{
                toast.success("Information Updated Successfully");
              }
            });
            setTimeout(() => history.push("/folder/:folderId"), 500);
        }
    }
  };
  return (
    <div>
      <form onSubmit={handleSubmit}>


        {/* imput field Enter a Specification */}
        {/* <label htmlFor='name'>Specification</label> */}
        <input type="text" id="name" name="name" placeHolder="Enter the Specification..." value={name || ""} onChange={handleInputChange} />


        {/* imput filed Enter Details */}
        {/* <label htmlFor='email'>Details</label> */}
        <input type="text" id="email" name="email" placeHolder="Enter the Details..." value={email || ""} onChange={handleInputChange} />

        <input type="submit" value={id ? "Update" : "Save"} />
      </form>
    </div>
  );
}

export default AddEdit;
