import React from "react";
import { Button } from "react-bootstrap";


const EditableRow = ({
  editFormData,
  handleEditFormChange,
  handleCancelClick,
}) => {
  return (
    <>
    <tr className="p-0 m-0">
      <td className="px-0 m-0">
        <input
          type="text"
          required="required"
          placeholder="Enter a Specification"
          name="fullName"
          value={editFormData.fullName}
          onChange={handleEditFormChange}
          className="w-75 form-control"
        ></input>
      </td>
      <td className="px-0 m-0">
        <input
          type="text"
          required="required"
          placeholder="Enter an Detail"
          name="address"
          value={editFormData.address}
          onChange={handleEditFormChange}
          className="w-75 form-control"
        ></input>
      </td>
      <td className="d-inline-flex">
        <Button type="submit" className="mx-1 btn-sm">Save</Button>
        <Button type="button" className="btn-sm" onClick={handleCancelClick}>
          Cancel
        </Button>
      </td>
    </tr>
    </>
  );
};

export default EditableRow;
