import React from "react"
import { Container } from "react-bootstrap"
import { useFolder } from "../../hooks/useFolder"
import AddFolderButton from "./AddFolderButton"
import AddFileButton from "./AddFileButton"
import Folder from "./Folder"
// import File from "./File"
import Navbar from "./Navbar"
import FolderBreadcrumbs from "./FolderBreadcrumbs"
import { useParams, useLocation } from "react-router-dom"
import { SRLWrapper } from "simple-react-lightbox";
import "../table-details/Table.css"
import TableDetails from "../table-details/TableDetails"
import Header from "../table-details/Header";
import { toast } from 'react-toastify';




export default function Dashboard() {
  const { folderId } = useParams()
  const { state = {} } = useLocation()
  const { folder, childFolders, childFiles } = useFolder(folderId, state.folder)

    //Delete Function
    const onDelete = (id) => {
      if(window.confirm("Are you sure that you wante to delete this ?")){
        folderId.remove((err) => {
          if(err){
            toast.error(err)
          }else{
            toast.success("Information deleted Successfully!");
          }
        })
      }
    }

  return (
    <>
      <Navbar />


      <Container  tainer fluid>
        <div className="d-flex justify-content-between align-item-top mx-4 my-0 ">
          <FolderBreadcrumbs currentFolder={folder} />
          {/* <Header currentFolder={folder}/> */}
          <AddFileButton currentFolder={folder} />
          <AddFolderButton currentFolder={folder} />
        </div>
        {childFolders.length > 0 && (
          <div className="flex-wrap d-flex justify-content-between m-4 ">
            {childFolders.map(childFolder => (
              <div
                key={childFolder.id}
                style={{ maxWidth: "250px" }}
                className="p-2"
              >
                <Folder folder={childFolder} />
              </div>
            ))}

          </div>
        )}
        {childFolders.length > 0 && childFiles.length > 0 && <hr />}
        {childFiles.length > 0 && (



          <div className="m-4 TableDetailsMaster d-flex ">

{childFolders.map(childFolder =>
  <button className='btn btn-delete' onClick={() => onDelete(childFolder.id)}>Delete</button>
  )}

            {/* --Table Details-- */}
              <div className="TableDetails p-0 ">
                {/* <div><h2></h2></div> */}
                {/* <div><h4 className="font-weight-bold p-4 wv-100">Information</h4></div> */}

              <div>
                {/* {console.log(folder.id)}
                {
                  while (condition) {

                  }

                } */}


                {/* {(() => {
                  if (TableDetails.length > 0 ) {
                    return (
                    null
                    )
                  } else {
                    return (
                    <TableDetails/>
                    )
                  }
                })()} */}
                {/* <TableDetails/> */}
              </div>



              </div>
            {/*--End Table Details-- */}

            {/*--Photo section-- */}
            <SRLWrapper className="SRLWrapper">
              <div id="app" className="">
                {childFiles.map(childFile => (
                  <div // key={childFile.id} style={{ maxWidth: "400px" }}
                    className="m-3"
                  >
                    {/* {<File file={childFile}/>} */}
                      <a href={childFile.url}><img src={childFile.url} id="photo"/></a>
                  </div>
              //--End of Photo section--

                ))}
              </div>
            </SRLWrapper>

          </div>

        )}
      </Container>
    </>
  )
}
